export default {
  data: function data() {
    return {
      boxshow: true,
      isshow: false,
      value: 1,
      list: [],
      radio: "1",
      checkedAll: false,
      sum: "",
      countAll: "",
      // 单个总价
      subCount: [] // bols: []

    };
  },
  methods: {
    del: function del() {
      var _this = this;

      for (var i = 0; i < this.list.length; i++) {
        if (this.list[i].bol) {
          var ids = this.list[i].id;
          this.axios.get("del", {
            params: {
              id: ids
            }
          }).then(function (res) {
            console.log(res);

            _this.select();
          });
        } else {
          console.log(222);
        }
      }
    },
    show: function show() {
      this.isshow = !this.isshow;
      this.boxshow = !this.boxshow;
    },
    // 判断是否全选
    judge: function judge() {
      var flag = true;
      var _iteratorNormalCompletion = true;
      var _didIteratorError = false;
      var _iteratorError = undefined;

      try {
        for (var _iterator = this.list[Symbol.iterator](), _step; !(_iteratorNormalCompletion = (_step = _iterator.next()).done); _iteratorNormalCompletion = true) {
          var item = _step.value;

          if (!item.bol) {
            flag = false;
          }
        }
      } catch (err) {
        _didIteratorError = true;
        _iteratorError = err;
      } finally {
        try {
          if (!_iteratorNormalCompletion && _iterator.return != null) {
            _iterator.return();
          }
        } finally {
          if (_didIteratorError) {
            throw _iteratorError;
          }
        }
      }

      if (flag) {
        this.checkedAll = true;
      } else {
        this.checkedAll = false;
      }
    },
    // 单选按钮
    checkedOne: function checkedOne(i) {
      // console.log(i)
      if (this.list[i].bol == false) {
        this.list[i].bol = true;
      } else {
        this.list[i].bol = false;
      }

      console.log(this.list[i].bol);
      this.judge();
    },
    // 全选按钮
    chooseAll: function chooseAll() {
      // console.log(this.checkedAll);
      if (this.list == null) {
        return;
      } else if (!this.checkedAll) {
        var _iteratorNormalCompletion2 = true;
        var _didIteratorError2 = false;
        var _iteratorError2 = undefined;

        try {
          for (var _iterator2 = this.list[Symbol.iterator](), _step2; !(_iteratorNormalCompletion2 = (_step2 = _iterator2.next()).done); _iteratorNormalCompletion2 = true) {
            var item = _step2.value;
            item.bol = true;
          }
        } catch (err) {
          _didIteratorError2 = true;
          _iteratorError2 = err;
        } finally {
          try {
            if (!_iteratorNormalCompletion2 && _iterator2.return != null) {
              _iterator2.return();
            }
          } finally {
            if (_didIteratorError2) {
              throw _iteratorError2;
            }
          }
        }
      } else {
        var _iteratorNormalCompletion3 = true;
        var _didIteratorError3 = false;
        var _iteratorError3 = undefined;

        try {
          for (var _iterator3 = this.list[Symbol.iterator](), _step3; !(_iteratorNormalCompletion3 = (_step3 = _iterator3.next()).done); _iteratorNormalCompletion3 = true) {
            var item = _step3.value;
            item.bol = false;
          }
        } catch (err) {
          _didIteratorError3 = true;
          _iteratorError3 = err;
        } finally {
          try {
            if (!_iteratorNormalCompletion3 && _iterator3.return != null) {
              _iterator3.return();
            }
          } finally {
            if (_didIteratorError3) {
              throw _iteratorError3;
            }
          }
        }
      }
    },
    jia: function jia(e, m) {
      var _this2 = this;

      var index = e.target.dataset.index;
      this.sub();

      if (this.list[index].count > 0) {
        this.list[index].count += m;
      } else {
        /////////////////////////////////////////////////////////////////////////////////////////////
        // 此处有bug需要调整
        this.list[index].count = 1;
      }

      var count = this.list[index].count;
      setTimeout(function () {
        var id = _this2.list[index].id;

        _this2.axios.get("jia", {
          params: {
            id: id,
            count: count
          }
        }).then(function (res) {
          _this2.select();
        });
      }, 5000);
    },
    select: function select() {
      var _this3 = this;

      this.axios.get("bag").then(function (res) {
        if (res.data.code == -1) {
          console.log("没有数据");
          _this3.boxshow = false;
          _this3.list = null;
          return;
        } else {
          _this3.boxshow = true;
          _this3.list = res.data.result;

          for (var i = 0; i < _this3.list.length; i++) {
            _this3.list[i].bol = false;
          }

          _this3.sub();
        } // console.log(this.list)

      });
    },
    sub: function sub() {
      var sum = 0;
      var countAll = 0;

      for (var i = 0; i < this.list.length; i++) {
        var count = Number(this.list[i].count);
        var discount = Number(this.list[i].discount);
        var sub = count * discount;
        sum += sub;
        countAll += count;
      }

      this.sum = sum;
      this.countAll = countAll;
    }
  },
  created: function created() {
    this.select();
  }
};
