export default {
  data: function data() {
    return {
      unameholder: "请输入用户名",
      upwdholder: "请输入密码",
      uname: "",
      upwd: ""
    };
  },
  methods: {
    login: function login() {
      //功能：完成用户登入操作
      // 1：获取用户输入用户名
      var u = this.uname; // 2：获取用户输入的密码

      var p = this.upwd; // 3：创建正则表达式验证用户名和密码

      var reg = /^[a-z0-9]{3,12}$/i; // 3~12位 字母和数字
      // 4.用户验证失败，提示短消息
      // 	// 5.密码验证失败 提示短消息

      !reg.test(u) ? this.$toast("用户名格式不正确") : !reg.test(p) ? this.$toast("密码格式不正确") : ""; // 6：发送ajax 请求axios ？

      var url = "login";
      var obj = {
        uname: u,
        upwd: p
      };
      this.axios.get(url, {
        params: obj
      }).then(function (res) {
        // 7.获取服务器返回结果
        console.log(res); // 7.1.登入失败 提示
        // 7.2.登入成功 跳转商品页面首页组件
      });
    }
  }
};
